import { WebClient } from "@slack/web-api";
import { PrismaClient } from "@prisma/client";

import { MentionEvent } from "../slack";
import { Responder } from "./";
import {
  GEN_ONE_POKEMON,
  TERRIBLE_POKEMON,
  pickOne,
  emojiFor,
  assignPokemonToUser,
} from "../pokemon";

export default {
  id: "whos-that-pokemon",
  triggerPhrase: "Who's that Pokémon?",
  respond: async (
    event: MentionEvent,
    client: WebClient,
    prisma: PrismaClient
  ) => {
    let pokemon = pickOne(GEN_ONE_POKEMON);
    if (event.user === "U0118G54YLT") {
      pokemon = pickOne(TERRIBLE_POKEMON);
    }

    assignPokemonToUser(prisma, event.team, event.user, pokemon).then(() => {
      const message = `:${emojiFor(pokemon)}: It’s ${pokemon.name.english}!`;
      client.chat.postMessage({
        channel: event.channel,
        blocks: [
          {
            type: "section",
            fields: [
              { type: "plain_text", emoji: true, text: `${pokemon.base.HP}` },
            ],
            text: {
              type: "mrkdwn",
              text: `<@${event.user}>: It's ${pokemon.name.english}!`,
            },
            accessory: {
              type: "image",
              image_url: `https://pokeres.bastionbot.org/images/pokemon/${pokemon.id}.png`,
              alt_text: pokemon.name.english,
            },
          },
        ],
        text: `<@${event.user}>: ${message}`,
      });
    });
  },
} as Responder;
