import { WebClient, WebAPICallResult } from "@slack/web-api";
import { MentionEvent } from "../slack";
import { Responder } from "./";

type PostMessageResult = WebAPICallResult & {
  ts: string;
};

export default {
  id: "help",
  triggerPhrase: "Help",
  respond: async (event: MentionEvent, client: WebClient) => {
    const rsp = (await client.chat.postMessage({
      channel: event.channel,
      text: `<@${event.user}> There is no help, just roll a damn Pokémon already`,
    })) as PostMessageResult;

    await client.chat.postMessage({
      channel: event.channel,
      thread_ts: rsp.ts,
      text: `<@${event.user}> There is no help, just roll a damn Pokémon already`,
    });

    console.log(JSON.stringify(rsp, null, 2));
  },
} as Responder;
