import { WebClient } from "@slack/web-api";
import { PrismaClient, Roll } from "@prisma/client";

import { MentionEvent } from "../slack";
import { Responder } from "./";
import { POKEMON, pickOne, emojiFor } from "../pokemon";

export default {
  id: "battle",
  triggerPhrase: "Battle",
  respond: async (
    event: MentionEvent,
    client: WebClient,
    prisma: PrismaClient
  ) => {
    const roll = await pokeForUser(prisma, event.team, event.user);
    const currentPokemon = POKEMON[roll.pokemonNumber - 1];

    if (currentPokemon == null) {
      await client.chat.postMessage({
        channel: event.channel,
        text: `Nah`,
      });
      return;
    }

    await client.chat.postMessage({
      channel: event.channel,
      text: `<@${event.user}>: Battling with a :${emojiFor(
        currentPokemon
      )}: ${JSON.stringify(event)}`,
    });

    Array.from(event.text.matchAll(/<@(\w+)>/)).forEach(async (m) => {
      console.log("user", m[1]);
      const user = await client.users.info({ user: m[1] });
      await client.chat.postMessage({
        channel: event.channel,
        text: `<@${event.user}>: User is ${JSON.stringify(user)}`,
      });
    });

    event.blocks.filter((b) => b.type === "user").forEach(async (b) => {});
  },
} as Responder;

const pokeForUser = async (
  prisma: PrismaClient,
  teamId: string,
  userId: string
): Promise<Roll> => {
  const r = await prisma.roll.findMany({
    where: { teamId, userId },
    orderBy: { createdAt: "desc" },
    take: 1,
  });
  return r[0];
};
