import { WebClient } from "@slack/web-api";
import { PrismaClient } from "@prisma/client";

import { MentionEvent } from "../slack";
import { Responder } from "./";
import { emojiFor, currentPokemonForUser } from "../pokemon";

export default {
  id: "query-latest-pokemon",
  triggerPhrase: "Who's my Pokémon?",
  respond: async (
    event: MentionEvent,
    client: WebClient,
    prisma: PrismaClient
  ) => {
    currentPokemonForUser(prisma, event.team, event.user).then((poke) => {
      console.log("poke is", poke);
      if (!poke) {
        client.chat.postMessage({
          channel: event.channel,
          text: `<@${event.user}>: You've not rolled a Pokémon yet`,
        });
      } else {
        client.chat.postMessage({
          channel: event.channel,
          text: `<@${event.user}>: Your last roll was :${emojiFor(poke)}: ${
            poke.name.english
          }`,
        });
      }
    });
  },
} as Responder;
